import React from 'react';
import ReactDOM from 'react-dom';
import './style.sass';
import openSocket from 'socket.io-client';


class Camera extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      img: null
    }
  }

  randomRGBValue() {
    return Math.floor(Math.random() * 256);
  }

  randomRGBImageArray(width, height) {
    let rgbdata = [];
    for(var i=0; i< width; i++){
      for(var j=0; j< height; j++){
        rgbdata.push([
          this.randomRGBValue(),
          this.randomRGBValue(),
          this.randomRGBValue()
        ]);
      }
    }
    console.log(rgbdata.toString());
    return rgbdata;
  }

  renderImg(rgbdata) {
    // convert rgbdata from server, paint it on a canvas and render it on the page
    const canvas = this.refs.canvas;
    let ctx = canvas.getContext('2d');

    for(var i=0; i< rgbdata.length; i++){
      for(var j=0; j< rgbdata[0].length; j++){	
        ctx.fillStyle = 'rgba('+
          rgbdata[i][j][0]+','+
          rgbdata[i][j][1]+','+
          rgbdata[i][j][2]+', 1)'; 
        ctx.fillRect( j, i, 1, 1 );
      }
    }

    console.log(canvas);

    this.setState({
      img: canvas // set image state which will render it
    });
  }

  componentWillMount() {
    // get rgbdata from server
    fetch('klateczka.txt')
    .then(response => response.text())
    .then(data => {
      console.log(data[0]);
      this.renderImg(
        this.randomRGBImageArray(200,100)
      );
    });
  }

  render() {
    return (
      <div>
        {/* <img src={ this.state.img } alt="ROV camera output"/> */}
        {/* <canvas width="200" height="100"></canvas> */}
        <canvas ref='canvas' width='200' height='100'></canvas>
      </div>
    );
  }
}

// ========================================

// const io = openSocket('http://localhost:3000');

ReactDOM.render(
  <Camera />,
  document.getElementById('root')
);
