import React from 'react';
import ReactDOM from 'react-dom';
import './style.sass';
import openSocket from 'socket.io-client';


class Camera extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      img: null
    }
  }

  async getData() {
    let imgData = await(
      await fetch('frame.txt')
    ).text();
    console.log(imgData);
    return imgData;
      // .then(res => res.text())
