      // });
    // return "http://robocik.pwr.edu.pl/src/img/rov_4_0/rov_underwater.png";
  }

  getImg() {
    let x = this.getData().then(field => {
      // console.log(field)
      return field;
    });
    console.log(x);
    return this.getData().then(field => {
      // console.log(field)
      return field;
    });
  }

  render() {
    return (
      <div>
        <img src={ this.getImg() } alt="ROV camera output"/>
      </div>
    );
  }
}

