import React from 'react';
import ReactDOM from 'react-dom';
import './style.sass';
import openSocket from 'socket.io-client';


class Camera extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      img: null
    }
  }

  getData() {
    let imgData;
    fetch('frame.txt')
    .then(data => data.text())
    .then(data => {
      imgData = "data:image/png;base64," + data;
      console.log(imgData);
      // this.setState({
      //   img: imgData
      // })
      return "http://robocik.pwr.edu.pl/src/img/rov_4_0/rov_underwater.png";
    });
    // return "http://robocik.pwr.edu.pl/src/img/rov_4_0/rov_underwater.png";
  }

  render() {
    return (
      <div>
        <img src={this.getData()} alt="ROV camera output"/>
      </div>
    );
  }
}

// ========================================

// const io = openSocket('http://localhost:3000');

ReactDOM.render(
  <Camera />,
  document.getElementById('root')
);
